INSERT INTO `services` VALUES (1,538505964955515,'Memory Minyan',NULL,577622941794896,'From their website: \"The world’s first dementia-friendly Shabbat prayer and Shabbat meal experience in a synagogue. Caregivers can invite old friends to worship and share a meal in a space they have shared for years.\"','1121803768506081','https://beyondmemory.org/memory-minyan/',NULL,NULL,'1253061813300180,1106914314143204,1285075174713673',NULL,NULL,NULL,NULL,NULL,'1235801167970102',NULL,NULL,NULL,'837398020781190',NULL,NULL),(2,568312753285074,'Broadway Chemists',NULL,NULL,'Free delivery, has prescription services, hygiene products, and cosmetics; Monday-Friday: 9 am - 7 pm, Saturday: 10 am -5 pm, Sunday: closed.','631352058738431','http://broadwaychemists.com/home/383515','bchemists@gmail.com',NULL,'890627628532068',NULL,NULL,NULL,NULL,NULL,'1320951424186281',NULL,NULL,NULL,'675276146694278',NULL,NULL),(3,578403399173032,'Paws & Reflect',NULL,757664453121327,'ONLY FOR CLIENTS OF ENCORE COMMUNITY SERVICES; \"Volunteers take our puppies to visit homebound elderly senior citizens in Manhattan. Visits occur on weekends, Saturday mornings or Sunday afternoons (once a week for 45 minutes, a commitment of 1 year). To participate in this program, you must live in Manhattan (the West Side, from Chelsea up to the 80\'s, depending on whether volunteer has a car).\"','686682455890039','www.puppiesbehindbars.com',NULL,NULL,'998681432266511',NULL,NULL,NULL,NULL,NULL,'543113728043543',NULL,NULL,NULL,'590507001886660',NULL,NULL),(4,588918566885363,'HomeBase Program to Prevent Homelessness',NULL,591336967347293,'From their website: \"Homelessness prevention program administered by Community-Based Organizations (CBOs) in high-need neighborhoods. The CBOs provide casework services and also help individuals and families locate existing community-based resources such as job training, child care, and anti-eviction legal services.\nYou are strongly encouraged to call your nearest HomeBase location before visiting. Depending on your address and situation, you may be advised to go to a different HomeBase office in your borough.\"','759572047570775','https://www1.nyc.gov/nyc-resources/service/6504/homebase-program-to-prevent-homelessness',NULL,NULL,'958412510535349',NULL,NULL,NULL,NULL,NULL,'1326189552461198',NULL,NULL,NULL,'1014326083250530',NULL,NULL),(5,597095425907055,'ComForCare',NULL,NULL,'\"Services are available around the clock, 24 hours a day, seven days a week, including holidays. We can provide same-day home care, transition-of-care, such as transportation home after a discharge, or assist with many other needs that arise when a family member is in crisis. We also provide support to hospitals and care facilities, including staffing hospital sitters if your loved one needs an extra level of attention.\"','777089940744581','https://comforcare.com/New-York/NYC','nyc@comforcare.com',NULL,'687567198750084',NULL,NULL,NULL,NULL,NULL,'663067304949311',NULL,NULL,NULL,'555271766673538',NULL,NULL),(6,597445276591735,'Escort Assistance Services',NULL,895690887235428,'\"Serving those who live, work, or go to school in Chelsea, with a focus on those in need, adult services for older adults age 55+,  staff speaks many languages including Spanish, Cantonese and Mandarin in addition to English. Must be a member of Penn South or live at the Hudson Guild NORC, Elliott-Chelsea Houses. Call for intake over the phone or in person.\"','609269880961062','https://hudsonguild.org/programs/adult-services/',NULL,NULL,'1119801442928622',NULL,NULL,NULL,NULL,NULL,'1307839956187600',NULL,NULL,NULL,'657516659707090',NULL,NULL),(7,577322070735769,'Elderly Project','VOLS',578716971059898,'\"The Elderly Project staff and pro-bono attorneys provide free legal advice, information, document drafting, and other brief services to low-income Manhattan residents aged 60 and over, and to the social workers and advocates who assist them. These services include direct counseling on critical issues involving housing, government benefits, and consumer debt, and the drafting and execution of wills, powers of attorney, and other essential life-planning documents. Discuss your legal question with one of our attorney referral counselors or submit it online. All information you provide will be kept confidential to the fullest extent possible under the law. Get referred to a screened and qualified lawyer or the right resource. Initial consultations are $35 or free, depending on the type of case, and last up to 30 minutes. We conduct 12 monthly walk-in legal advice and brief service clinics at Manhattan senior centers. At the clinics, seniors can discuss their legal problems with an attorney in confidence, receive advice and brief services, and, if appropriate, obtain a referral to a volunteer lawyer for representation. Seniors receive advice on legal matters in the areas of housing, consumer debt, public benefits, and life-planning documents.  Referrals are also provided to organizations that can provide representation in these and other types of legal matters.\"','985066684836990','http://www.volsprobono.org/projects/elderly-project',NULL,NULL,'1107228321502442',NULL,NULL,NULL,NULL,NULL,'1139481046774619',NULL,NULL,NULL,'613017381757593',NULL,NULL),(8,617871807240903,'Golden Steps Co-Op\n\n',NULL,NULL,'\"Golden Steps offers the best of both worlds, providing people needing home care with a direct personal relationship that is backed by the cooperative’s structure, insurance, training and supervision. Clients interview and pay our members directly, and members in turn pay a monthly quota to the non-profit cooperative to cover its costs.\"','1115038632022507','www.goldensteps.coop','info@goldensteps.coop',NULL,'687567198750084',NULL,NULL,NULL,NULL,NULL,'958279905939824',NULL,NULL,NULL,'1097933846208959',NULL,NULL),(9,566109878776774,'One-Stop',NULL,932368348148295,'Case assistance, care management, advocacy training, and home care for a walk-in senior population as well as homebound seniors','1266138041732190','www.jasa.org/community/one-stop-at-jasa',NULL,NULL,'819444632829102',NULL,NULL,NULL,NULL,NULL,'1193391740373847',NULL,NULL,NULL,'605846092316127',NULL,NULL),(10,564760337317731,'Memory Center Caregiver Support Group',NULL,844903847651421,'From their website: \"In conjunction with Caring Kind, an evening group is held at the New York Memory Center. The group meets twice a month on the first and third Wednesdays, and sessions are free but you must pre-register.\"','776030591004985','www.nymemorycenter.org','mchrist@nymemorycenter.org',NULL,'673898301722674',NULL,NULL,NULL,NULL,NULL,'800211077624777,1318904254156917',NULL,NULL,NULL,'1292197970730746',NULL,NULL),(11,600470950137477,'Cemetery Visit Program',NULL,1146332029502995,'Hosts a program in August (call in the end of July to find out exact date and which cemeteries they go to), there are no requirements to participate.','780506072615648','http://ujceastside.org/ujc-senior-services/',NULL,NULL,'1137942925140598',NULL,NULL,NULL,NULL,NULL,'726325498600801',NULL,NULL,NULL,'619641711895444',NULL,NULL),(12,617663215129537,'Addiction Institute',NULL,920174442924566,'From their website: \"Centers that treat people with addictions to alcohol, substances, and behaviors such as compulsive eating or gambling. Multiple locations in Manhattan, at Mount Sinai St. Luke\'s, Mount Sinai West, Mount Sinai Beth Israel, and The Mount Sinai Hospital. Our wide range of treatments relies on research into emerging therapies. We personalize our treatment plans to meet the individual needs of you or your loved ones.\"','1242779942144581','https://www.mountsinai.org/locations/addiction-institute',NULL,NULL,'1112870426305700',NULL,NULL,NULL,NULL,NULL,'1247621772400170',NULL,NULL,NULL,'880683347691640',NULL,NULL),(13,614110826947242,'Lions Eyeglass Recycling Center',NULL,936275506302860,'Look up donation drop-off sites online or mail donations to recycling center; reading glasses and sunglasses are donated to people in need and prescription glasses are cataloged so those in need can find a matching prescription to suit them free of charge. ','852030822667031','http://www.njlerc.org/donations.html',NULL,NULL,'1084050103649570',NULL,NULL,NULL,NULL,NULL,'1052685883168977',NULL,NULL,NULL,'1254512533201976',NULL,NULL),(14,576627045489309,'Self Help-Project Pilot',NULL,911077302205989,'Case management agency, serving zip codes: 10002, 10003, 10005, 10009, 10010, 10012, 10016, 10017, 10021, 10022, 10024, 10025, 10026, 10038, 10044','837099011829572','www.selfhelp.net','info@selfhelp.net',NULL,'819444632829102',NULL,NULL,NULL,NULL,NULL,'1155750971369497',NULL,NULL,NULL,'758370601759800',NULL,NULL),(15,608115316855943,'Union Settlement Transportation Program',NULL,1331740916278162,'From their website: \"Our Transportation program provides East Harlem seniors with an array of trips for necessities, such as visiting the pharmacy or the bank, and group recreational excursions. The program enhances many aspects of our senior center programming, enabling us to take seniors to activities, employment or volunteer work sites, and computer classes.\"','929831456744199','https://unionsettlement.org/senior-services/',NULL,NULL,'977021400698839',NULL,NULL,NULL,NULL,NULL,'658797319701688,819686817839207',NULL,NULL,NULL,'1014326083250530',NULL,NULL),(16,609585134372649,'Encore Community Services',NULL,NULL,'Program is run with a small number of volunteers. Requirements: for seniors 60 and up, not home-bound, lack assistance and/or formal support (home health aide or family), the use of a wheelchair, walker, or cane is okay.','1114084783135534','http://encorecommunityservices.org/outreach-programs',NULL,NULL,'1119801442928622,819444632829102',NULL,NULL,NULL,NULL,NULL,'1234004209938688',NULL,NULL,NULL,'971484030543087',NULL,NULL),(17,609956463752073,'Housing Assistance Program',NULL,1147382684224778,'Advocacy and referrals for housing resources for people with disabilities.','885050653586052','www.adaptcommunitynetwork.org','projectconnect@adaptcommunitynetwork.org',NULL,'958412510535349',NULL,NULL,NULL,NULL,NULL,'1098357088666608',NULL,NULL,NULL,'1208664607201611',NULL,NULL),(18,623741258815903,'Project Metropair',NULL,1027449163057187,'From their website: \"For renters, homeowners, or qualified condominium or co-op owners, provides free minor home repairs to income-eligible homeowners or tenants ages 60 and older.\nThese repairs include: minor plumbing, minor carpentry and electrical work, caulking and weather-stripping of windows and doors, installation or repair of door locks and chains, major work, such as the installation of a ramp, is not provided.\"','706307500890092','https://www.metcouncil.org/project-metropair',NULL,NULL,'1123433776824532',NULL,NULL,NULL,NULL,NULL,'1192649972762773',NULL,NULL,NULL,'787498608599908',NULL,NULL),(19,629993267149099,'Karen Horney Clinic',NULL,NULL,'From their website: \"Offers low-cost, long-term talk therapy (psychoanalytic and psycho-therapeutic services). Patients without insurance pay $75 for the initial Intake appointment and sliding scale for ongoing therapy, accepts Medicare, Medicaid, and other insurances.\"','1201768948730770','http://www.karenhorneyclinic.org/','TheClinic@karenhorneyclinic.org',NULL,'1112870426305700',NULL,NULL,NULL,NULL,NULL,'744593448989688',NULL,NULL,NULL,'630587500521147',NULL,NULL),(20,629334985786738,'Senior Transportation Program',NULL,813598238487651,'From their website: \"Provides transportation for senior citizens residing in Community Districts 1, 2, 3, 4 & 7 in Queens County (districts include all of Northern Queens, specifically, Astoria, Long Island City, Steinway, Sunnyside, Woodside, Jackson Heights, Elmhurst Corona, Flushing, Whitestone and College Point). Both van and car service is used to accommodate the needs of the clients that utilize the program. Van service provides group transportation for seniors to various senior centers, as well as for group shopping and day trips. Car service provides private transportation to physically handicapped or extremely frail seniors for medical appointments.\"','878692164769425','http://hss.hanac.org/hanac.org/transportation.php',NULL,NULL,'977021400698839',NULL,NULL,NULL,NULL,NULL,'589885648146922',NULL,NULL,NULL,'1226187280167552',NULL,NULL),(21,633984930893350,'Upper Manhattan Partnership For Senior Independence','ISABELLA',858950316303765,'From their website: \"Provides free case management services to older adults living in Inwood, Washington Heights, Central and West Harlem (Community Boards 9, 10 and 12). If you are 60 years or older and live in the neighborhoods identified above - we can help assess your needs and link you with an array of supports and services such as Home Care, Social Day Care, shopping and chore services, counseling and home delivered meals.\"','1118938994250126','http://www.isabella.org/Isabella/Programs/CaseManagement.aspx',NULL,NULL,'819444632829102',NULL,NULL,NULL,NULL,NULL,'1001326188914226',NULL,NULL,NULL,'1208700863367720',NULL,NULL),(22,602743733951660,'BronxWorks Senior Services',NULL,1064833148991402,'Senior centers, senior housing assistance program, minor repair program, elder abuse services, senior mental health programs, seniors homelessness prevention project; also has Single Stop Hotline; Offers walk-in and telephone services to help Bronx residents resolve problems.  Our offices are staffed by skilled, compassionate professionals who are specialists in housing, government benefits, consumer rights, immigration, senior services, and other matters.  All walk-in personnel speak both English and Spanish.','637832190546814','http://www.bronxworks.org/',NULL,NULL,'652340976007902,958412510535349,629629464544122,1123433776824532,1197693117142860,843777382088472,1112870426305700',NULL,NULL,NULL,NULL,NULL,'569823640480746',NULL,NULL,NULL,'1231425990546332',NULL,NULL),(23,600095867391854,'Senior Law-Goldfarb Abrandt Salzman & Kutzin, LLP',NULL,NULL,'A resource site for seniors, caregivers, and professionals that provides info. on: elder law, Medicare, Medicaid, Medicaid planning, guardianship, estate planning, trusts, and the rights of the elderly and disabled. ','922489615301409','www.seniorlaw.com',NULL,NULL,'1107228321502442',NULL,NULL,NULL,NULL,NULL,NULL,NULL,NULL,NULL,'1341861553660117',NULL,NULL),(24,613454685448330,'Falk Surgical',NULL,NULL,'Assistive devices for rent or sale, other medical supplies.','1177298161182036','www.falksurgical.com',NULL,NULL,'1293277534665673,650194788641739',NULL,NULL,NULL,NULL,NULL,'1115999700397262',NULL,NULL,NULL,'912264230832936',NULL,NULL),(25,619582368851776,'Legal Services NYC Access Line',NULL,NULL,'From their website: \"Hotline for general legal info & advice: Legal Services NYC Access Line serves low income residents of NYC and is open Monday through Friday from 10am to 4pm. (Please note that Bronx Legal Services, Brooklyn Legal Services, Manhattan Legal Services, Queens Legal Services and Staten Island Legal Services are programs of Legal Services NYC.) Open Monday through Friday from 10 am to 4 pm. Our neighborhood-based offices and outreach sites across all five boroughs.\" ','1285020855329210','http://www.legalservicesnyc.org/',NULL,NULL,'1107228321502442',NULL,NULL,NULL,NULL,NULL,'1187447589912028',NULL,NULL,NULL,'1122913756846010',NULL,NULL),(26,627967187993671,'New Lifestyles Area Guide to Senior Residences and Care Options',NULL,NULL,'An online and print resource for information about independent living facilities in New York City.','1314783901788814','https://www.newlifestyles.com/senior-living-and-care-options/independent-living/new-york-city-ny',NULL,NULL,'924951065612317,973172406940749',NULL,NULL,NULL,NULL,NULL,'1268503226823568',NULL,NULL,NULL,'1014326083250530',NULL,NULL),(27,645296352943315,'International Low Vision Support Group\n\n',NULL,NULL,'From their website: \"Free low-vision telephone support group, year-round program of monthly group support sessions held over the telephone. It is designed for low vision seniors in the United States who have no access to the Internet or cannot attend a live support group. A session typically includes a brief update on recent research and developments, followed by listening to and discussing the current month’s recorded ILVSG presentation. See the Schedule of Presentations. Each session will include the moderator and an average of 8-12 registered members. Members who wish to do so may share their phone numbers with others in the group for personal communication outside of the scheduled sessions. This aspect of the program is optional, with phone numbers otherwise remaining totally private.\"','982966126345790','http://www.mdsupport.org/support/telesupport/',NULL,NULL,'1291763496785325,1275784767796871',NULL,NULL,NULL,NULL,NULL,'1081963400559344',NULL,NULL,NULL,'839666236357292',NULL,NULL),(28,623181776850388,'Dementia Care Coach Program',NULL,576935766925878,'\"ReServe program coaches are mature, trustworthy, trained to provide care to persons with dementia; assistance in caring for someone with dementia, trained Care Coaches help balance whole care team offering education support and compassion after learning of dementia diagnosis; call for an assessment.\"','1099067423819327,964719380626077','www.reserveinc.org','info@reserveinc.org',NULL,'1253061813300180,1106914314143204,1285075174713673',NULL,NULL,NULL,NULL,NULL,'996946449389014',NULL,NULL,NULL,'1014326083250530,1014326083250530',NULL,NULL),(29,632044731493538,'Marlena Books',NULL,NULL,'From their website: \"Meaningful leisure and cognitive stimulation in the form of reading, features mature content at an appropriate reading level in order for individuals to continue their love of reading while maintaining their dignity.\"','1230155399654763','www.marlenabooks.com','info@marlenabooks.com',NULL,'1253061813300180,1106914314143204,1285075174713673',NULL,NULL,NULL,NULL,NULL,'1177931873752968',NULL,NULL,NULL,'1014326083250530',NULL,NULL),(30,635251908156359,'PEARLS (Program to Encourage Active Rewarding Lives)',NULL,613407436114821,'Free program that helps seniors with depression, short-term (approximately 6 visits); for seniors living in Queens and Manhattan.','875358829531470','https://www.vnsny.org/how-we-can-help/community-programs/community-mental-health-services/pearls-program-encourage-active-rewarding-lives/',NULL,NULL,'1112870426305700',NULL,NULL,NULL,NULL,NULL,'985649835819054',NULL,NULL,NULL,'1014326083250530',NULL,NULL),(31,637223360115025,'Pick Up Please',NULL,868407464886686,'From their website: \"Pickup Please offers free donation pickup service in New York. Get started by scheduling a pickup online. We will send a truck to your house — often within 24 hours if you like. We accept almost any type of item (clothing, toys, shoes, books, electronics, small furniture, and small household items). Donations benefit Veterans by supporting the Vietnam Veterans of America. *Please note – As a general rule, we only accept items that are small and light enough for one person to easily lift and carry.\"','943700786016099','https://pickupplease.org/donate-clothing-in-new-york/','info@pickupplease.org',NULL,'1084050103649570',NULL,NULL,NULL,NULL,NULL,'1059669697911851',NULL,NULL,NULL,'1014326083250530',NULL,NULL),(32,651515047921999,'Memory Tree',NULL,NULL,'For seniors with mild memory loss; all participants must schedule an interview prior to attending the program since the program is not equipped to handle “drop-ins.” If you would like to schedule an appointment please contact Elizabeth Fine.','1242044848402426,791971416713612','http://thememorytree.org',' info@thememorytree.org',NULL,'1253061813300180,1106914314143204,1285075174713673',NULL,NULL,NULL,NULL,NULL,'1158151045074969',NULL,NULL,NULL,'750348996086948,967252529008278',NULL,NULL),(33,610811374021996,'Catholic Guild for the Blind',NULL,789022028516981,'\"Travel/orientation and mobility, low-vision services and devices, case management, special services for seniors aged 55+ who are legally blind including: techniques for safe meal preparation, equipment specially designed to help people with low vision manage everyday chores, home organization assistance to make it easier to access belongings, tips for banking and paying bills, special labeling of clothes for easier identification, mobility training to safely access homes and neighborhoods, help with personal and health care needs, access to community resources.\"','816282644815025','https://catholiccharitiesny.org/blog/free-services-visually-impaired',NULL,NULL,'1291763496785325',NULL,NULL,NULL,NULL,NULL,'1215733560327150',NULL,NULL,NULL,'988983663682670',NULL,NULL),(34,615943866885675,'CaptionCall',NULL,NULL,'Phone captioning service','1164235597218399','https://captioncall.com/',NULL,NULL,'673030812970350',NULL,NULL,NULL,NULL,NULL,'961271994696097',NULL,NULL,NULL,'1014326083250530',NULL,NULL),(35,637039044245130,'Disposophobia',NULL,938988071113048,'\"Contents restoration management firm,\" Assists with de-cluttering or with extreme hoarding cases.','950758333744926','disp.theplan.com',NULL,NULL,'796847632116140,941628219469820',NULL,NULL,NULL,NULL,NULL,'1272931722141317',NULL,NULL,NULL,'1014326083250530',NULL,NULL),(36,640203880194694,'VillageCare at 46 and Ten',NULL,NULL,'From their website: \"Supportive living environment for seniors 62 or older. Apartments have a complete kitchen, which includes a refrigerator and stove. Air conditioners are provided in each apartment. Available services: weekly housekeeping and linen change, three meals a day, served restaurant-style, case management services, medication management, personal care/home health aide services, recreation activities, 24-hour security, 24-hour concierge and security service, personal emergency response call systems in each apartment.\" ','578828441433069','http://www.46and10village.org/',NULL,NULL,'924951065612317,973172406940749',NULL,NULL,NULL,NULL,NULL,'1207432936856041',NULL,NULL,NULL,'663356145416291',NULL,NULL),(37,660751014391174,'Integrity Senior Services Counseling','ISS',NULL,'In-home counseling, accepts Medicaid and Medicare.','1013220570111211,890414756577419','http://www.integrityseniorservices.com/Services.html',NULL,NULL,'819444632829102,1112870426305700',NULL,NULL,NULL,NULL,NULL,NULL,NULL,NULL,NULL,'1071300229733977,849507068539821',NULL,NULL),(38,667608398377019,'Encore Friendly Visiting',NULL,667859365781949,'\"Must have case management through DFTA agency to use the program; Matches volunteers with home-bound seniors in need of companionship. Volunteers visit for one hour on a weekly basis, forging friendships and helping to alleviate loneliness.\"','1114084783135534','www.encorecommunityservices.org/outreach-programs/',NULL,NULL,'1177309407177568',NULL,NULL,NULL,NULL,NULL,'746721853277288',NULL,NULL,NULL,'971484030543087',NULL,NULL),(39,664390395388298,'Health Insurance Information, Counseling and Assistance - HIICAP','HIICAP',1266739036051769,'\"HIICAP provides free, accurate and objective information, counseling, assistance and advocacy on Medicare, private health insurance, and related health coverage plans. HIICAP helps people with Medicare, their representatives, or persons soon to be eligible for Medicare.\"','1099830782498527','https://aging.ny.gov/healthbenefits/index.cfm',NULL,NULL,'629629464544122',NULL,NULL,NULL,NULL,NULL,'1249215487736961',NULL,NULL,NULL,'1080607873311324',NULL,NULL),(40,676064279658089,'Search & Care Volunteer Program',NULL,1072887075663062,'From their website: \"Companionship, visiting, and conversation, shopping, prescription pickups, and errands—particularly for the homebound, strolls through the neighborhood, technology tutorials (internet, email, e-books), socialization and entertainment activities (e.g. playing board games)\" for seniors residing in Manhattan’s Yorkville, Carnegie Hill, and East Harlem communities (catchment area is E. 86th to E.138th Streets, Eas